    override func viewWillAppear(_ animated: Bool) {
        errorMessage.isHidden = true
    }
    
    override func viewDidLoad() {
        super.viewDidLoad()
 //       tableView.delegate = self
 //       tableView.dataSource = self
        textField.delegate = self
    }
    
    func textFieldShouldReturn(_ textField: UITextField) -> Bool {
        resultButtonAction(self)
        return true
        
    }
    
    func getCountryCode(name: String) {
        countryCode.text = code["\(textField.text!)"]
       
    }
    
    func getCountryFlag(name: String) {
        countryFlag.text = flag["\(textField.text!)"]
    }

    @IBAction func resultButtonAction(_ sender: Any) {
        if textField.text == "" {
            return
        }
        self.getCountryCode(name: textField.text!)
        self.getCountryFlag(name: textField.text!)
        if countryCode.text == "" {
            errorMessage.isHidden = false
        } else if countryFlag.text == "" {
            errorMessage.isHidden = false
        }
        errorMessage.isHidden = true
        textField.resignFirstResponder()
    }
    
